/* Profile: First Aid Color Scheme || Satus: Saved */ 


html { overflow: scroll; overflow-x: hidden; }
/* CONTAINER FOR YOUR CARDS */
#kard {
    padding: 0px 0px;
    max-width: 950px; /* CHANGE CARD SIZE HERE */
    margin: 0 auto; /* CENTERS THE CARD IN THE MIDDLE OF THE WINDOW */
    word-wrap: break-word; /* BREAKS UP LONG WORKS */
}


/* APPLIES TO THE WHOLE CARD */
.card {
    font-family: Vernada;
    font-size: 28px; /* FONT SIZE */
    text-align: center; /* ALIGN TEXT */
    color: black; /* FONT COLOR */
    line-height: 1.2em;
    background-color: #FEFCF9; /* BACKGROUND COLOR Orange FDFBF6 blue/white FAFAFA  */
}
/* STYLE FOR CLOZE DELETIONS */
.cloze, .cloze b, .cloze u, .cloze i { font-weight: bold; color: #993330 !important;}

.night_mode .cloze  { font-weight: bold; color: MediumSeaGreen !important;}
.night_mode .cloze b { font-weight: bold; color: MediumSeaGreen !important;}
.night_mode .cloze u { font-weight: bold; color: MediumSeaGreen !important;}
.night_mode .cloze i { font-weight: bold; color: MediumSeaGreen !important;}


/* STYLE FOR EXTRA PORTION ON BACK OF CARD */
#extra { font-size: 26px; color: black; font-style: italic;  }
#extra i { font-size: 26px; color: black; font-style: italic;  }

.night_mode #extra { font-size: 26px; color: white !important; font-style: italic; }
.night_mode #extra i { font-size: 26px; color: white !important; font-style: italic; }

/* STYLE TAGS TO APPEAR WHEN HOVERING OVER TOP OF CARD */
.tags { 
    color: #A6ABB9;
    opacity: 0;
    font-size: 10px; 
    width: 100%;
    text-align: center;
    text-transform: uppercase; 
    position: fixed; 
    padding: 0; 
    top:0;  
    right: 0;}
.tags:hover { opacity: 1; position: fixed;}


/* IMAGE STYLE */
img { display: block; max-width: 100%; max-height: 650px; margin-center: auto; margin: 10px auto 0 auto;
box-shadow: 3px 6px 15px 1px rgba(0, 0, 0, 0.2), 3px 1px 20px 1px rgba(0, 0, 0, 0.19)

}

img:active { width: 100%;}
tr {font-size: 12px; }



/* COLOR ACCENTS FOR BOLD-ITALICS-UNDERLINE */
b {color: #306299; !important; } /* BOLD STYLE */
u { text-decoration: none; color: #D18835 ;} /* UNDERLINE STYLE */
i  { color: #309699; } /* ITALICS STYLE */
a { color: LightGray !important; text-decoration: none; font-size: 10px; font-style: normal; } /* LINK STYLE */

.night_mode b { color: #C695C6; !important; } /* BOLD STYLE */
.night_mode u { text-decoration: none !important; color: #5EB3B3; !important;} /* UNDERLINE STYLE */
.night_mode i  { color: IndianRed !important; } /* ITALICS STYLE */
.night_mode a { color: LightGray !important; text-decoration: none; font-size: 10px; font-style: normal; }

/* ADJUSTMENT FOR MOBILE DEVICES */
.mobile .card { color: black; background-color: #FDFBF6; } 
.mobile .tags:hover { opacity: 1; position: relative;}
.mobile .card img:active { width: inherit; max-height: none;}
}

